import React, { useRef, useState } from "react";
import classes from "./Checkout.module.css";

function isEmpty(value) {
    return value.trim() === "";
}

function isSixChars(value) {
    return value.trim().length === 6;
}

function AddMeal(props) {
    const [formInputsValidity, setFormInputsValidity] = useState({
        title: true,
        description: true,
        price: true,
    });
    const titleInputRef = useRef();
    const descriptionInputRef = useRef();
    const priceInputRef = useRef();

    function confirmHandler(event) {
        event.preventDefault();
        const enteredTitle = titleInputRef.current.value;
        const enteredDescription = descriptionInputRef.current.value;
        const enteredPrice = priceInputRef.current.value;

        const enteredTitleIsValid = !isEmpty(enteredTitle);
        const enteredDescriptionIsValid = !isEmpty(enteredDescription);
        const enteredPriceIsValid = !isSixChars(enteredPrice);

        setFormInputsValidity({
            title: enteredTitleIsValid,
            description: enteredDescriptionIsValid,
            price: enteredPriceIsValid,
        });

        const formIsValid =
            enteredTitleIsValid && enteredDescriptionIsValid && enteredPriceIsValid;

        if (!formIsValid) {
            return;
        }
        props.onConfirm({
            title: enteredTitle,
            description: enteredDescription,
            PRICE: enteredPrice,
        });
    }
    return ( <
            form onSubmit = { confirmHandler } >
            <
            div className = { `${classes.control} ${
          formInputsValidity.name ? "" : classes.invalid
        }` } >
            <
            label htmlFor = "name" > Title < /label> <
            input type = "text"
            id = "name"
            ref = { titleInputRef }
            /> {!formInputsValidity.name && < p > Please enter a valid name < /p>} < /
            div > <
            div className = { `${classes.control} ${
          formInputsValidity.street ? "" : classes.invalid
        }` } >
            <
            label htmlFor = "des" > Description < /label> <
            input type = "text"
            id = "des"
            ref = { descriptionInputRef }
            /> {!formInputsValidity.street && < p > Please enter a valid street < /p>} < /
            div > <
            div className = { `${classes.control} ${
          formInputsValidity.postalCode ? "" : classes.invalid
        }` } >
            <
            label htmlFor = "price" > Price < /label> <
            input type = "text"
            id = "price"
            ref = { priceInputRef }
            /> {!formInputsValidity.postalCode && ( <
                p > Please enter a valid PostalCode < /p>
            )
        } <
        /div>

    <
    div className = { classes.actions } >
        <
        button type = "button"
    onClick = { props.onCancel } >
        Cancel <
        /button> <
    button className = { classes.submit } > Confirm < /button> < /
        div > <
        /form>
);
}

export default AddMeal;
