import React, { useState } from "react";
import { StartFirebase } from "../../firebase";
import { ref, set, get, update, remove, child } from "firebase/database";
import classes from "../Cart/Checkout.module.css";
import Card from "../UI/Card";

function ManageMeal(props) {
  const [state, setState] = useState({
    db: "",
    title: "",
    description: "",
    price: "",
  });
  setState({ db: StartFirebase() });

  return (
    <div className={classes.container}>
      <Card>
        <h2> Meals Management </h2>{" "}
        <div className={classes.control}>
          <label htmlFor="title"> Title </label>{" "}
          <input
            type="text"
            id="title"
            value={state.title}
            onChange={(e) => {
              setState({ title: e.target.value });
            }}
          />{" "}
        </div>{" "}
        <div className={classes.control}>
          <label htmlFor="des"> Description </label>{" "}
          <input
            type="text"
            id="des"
            value={state.description}
            onChange={(e) => {
              setState({ description: e.target.value });
            }}
          />{" "}
        </div>{" "}
        <div className={classes.control}>
          <label htmlFor="price"> Price </label>{" "}
          <input
            type="number"
            id="price"
            min={1}
            max={5}
            value={state.price}
            onChange={(e) => {
              setState({ price: e.target.value });
            }}
          />{" "}
        </div>{" "}
        <div className={classes.actions}>
          <button className={classes.submit}> Add </button>{" "}
          <button className={classes.submit}> Delete </button>{" "}
          <button className={classes.submit}> Edit </button>{" "}
        </div>{" "}
      </Card>{" "}
    </div>
  );
}

export default ManageMeal;
