import React, { useRef, useState } from "react";
import classes from "../Cart/Checkout.module.css";
import Card from "../UI/Card";

function isEmpty(value) {
  return value.trim() === "";
}

function ManageMeal(props) {
  const [formInputsValidity, setFormInputsValidity] = useState({
    title: true,
    description: true,
    price: true,
  });
  const titleInputRef = useRef();
  const descriptionInputRef = useRef();
  const priceInputRef = useRef();

  function confirmHandler(event) {
    event.preventDefault();
    const enteredTitle = titleInputRef.current.value;
    const enteredDescription = descriptionInputRef.current.value;
    const enteredPrice = priceInputRef.current.value;

    const enteredTitleIsValid = !isEmpty(enteredTitle);
    const enteredDescriptionIsValid = !isEmpty(enteredDescription);
    const enteredPriceIsValid = !isEmpty(enteredPrice);

    setFormInputsValidity({
      title: enteredTitleIsValid,
      description: enteredDescriptionIsValid,
      price: enteredPriceIsValid,
    });

    const formIsValid =
      enteredTitleIsValid && enteredDescriptionIsValid && enteredPriceIsValid;

    if (!formIsValid) {
      return;
    }
    props.onConfirm({
      title: enteredTitle,
      description: enteredDescription,
      PRICE: enteredPrice,
    });
  }
  return (
    <div className={classes.container}>
      <Card>
        <h2> Meals Management </h2>{" "}
        <form onSubmit={confirmHandler}>
          <div
            className={`${classes.control} ${
              formInputsValidity.title ? "" : classes.invalid
            }`}
          >
            <label htmlFor="name"> Title </label>{" "}
            <input type="text" id="name" ref={titleInputRef} />{" "}
            {!formInputsValidity.title && (
              <p className={classes.invalid}> Please enter a valid title </p>
            )}{" "}
          </div>{" "}
          <div
            className={`${classes.control} ${
              formInputsValidity.description ? "" : classes.invalid
            }`}
          >
            <label htmlFor="des"> Description </label>{" "}
            <input type="text" id="des" ref={descriptionInputRef} />{" "}
            {!formInputsValidity.description && (
              <p className={classes.invalid}>
                {" "}
                Please enter a valid description{" "}
              </p>
            )}{" "}
          </div>{" "}
          <div
            className={`${classes.control} ${
              formInputsValidity.price ? "" : classes.invalid
            }`}
          >
            <label htmlFor="price"> Price </label>{" "}
            <input
              type="number"
              id="price"
              min={1}
              max={5}
              ref={priceInputRef}
            />{" "}
            {!formInputsValidity.price && (
              <p className={classes.invalid}> Please enter a valid price </p>
            )}{" "}
          </div>{" "}
          <div className={classes.actions}>
            <button className={classes.submit}> Add </button>{" "}
            <button className={classes.submit}> Delete </button>{" "}
            <button className={classes.submit}> Edit </button>{" "}
          </div>{" "}
        </form>{" "}
      </Card>{" "}
    </div>
  );
}

export default ManageMeal;
