import React from "react";
import classes from "./Admin.module.css";
import { AuthProvider, useAuth } from "../../store/AuthContext";
import ExictingMeals from "./ExictingMeals";
import AddMeal from "./AddMeal";
import { Link } from "react-router-dom";

export default function Admin() {
  const { currentUser } = useAuth();
  return (
    <>
      <AuthProvider>
        <p>hello</p>
        <header className={classes.header}>
          <h2> Hello {currentUser && currentUser.email} </h2>{" "}
        </header>{" "}
      </AuthProvider>
      <div className={classes.mainContainer}>
        <h2 className={classes.head}>Meals Management</h2>
        <button className={classes.head}>
          <Link to="/manage-meal"></Link>Manage existing meals :
        </button>
        <ExictingMeals />
        <button className={classes.head}>
          <Link to="/add-meal"></Link>Add a new meal :
        </button>
        <AddMeal />
      </div>
    </>
  );
}
